pragma solidity ^0.4.23;

contract Donation {

    // 捐款人
    struct Donor{
        address donor_address;
        uint donate_amount;
        uint donate_time;
    }

    address public owner; // 援助計畫單位
    string public projectName; // 援助計畫名稱
    uint public numDonor; // 捐款人數
    uint duration = 30 days; // 援助計畫持續時間
    uint start_donation_time; // 計畫開始時間(Unix Time)
    bool stop_donation; // 每月owner將捐款送出
    uint public totalAmount; // 捐款總額

    mapping(uint => Donor) public donors;
    mapping(address => uint[]) public donor_money;

    event Donated(address donor, uint amount, uint time, uint num, uint totalamount);
    event TransferDonation(address beneficiary, uint amount);
    event DonorMoney(address donor, uint[] amount);

    modifier onlyOwner() {
        require(msg.sender == owner);
        _;
    }

    constructor(string name) public {
        owner = msg.sender;
        projectName = name;
        numDonor = 0;
        totalAmount = 0;
        start_donation_time = now;
        stop_donation = false;
    }

    function donate() public payable {
        require(!stop_donation);

        Donor storage person = donors[numDonor++];
        person.donor_address = msg.sender;
        person.donate_amount = msg.value;
        person.donate_time = now;
        totalAmount += person.donate_amount;
        donor_money[person.donor_address].push(person.donate_amount);

        emit Donated(person.donor_address, person.donate_amount, person.donate_time, numDonor, totalAmount);
    }

    function checkMoney(address donor) public {
        emit DonorMoney(donor, donor_money[donor]);
    }

    function transferDonation(address beneficiary) public onlyOwner {
        require(!stop_donation);
        // require(now >= (start_donation_time + duration));

        stop_donation = true;
        beneficiary.transfer(totalAmount);

        emit TransferDonation(beneficiary, totalAmount);
    }

    function kill() public onlyOwner {
        selfdestruct(owner);
    }
}
